<script type="text/javascript">

function truncate(string, length, delimiter) {
   delimiter = delimiter || "&hellip;";
   return string.length > length ? string.substr(0, length) + delimiter : string;
};

const themes_wp_org_popular = 'https://www.wpsalenow.com/api/wp-org/themes/popular/';
const themes_envato_popular = 'https://www.wpsalenow.com/api/envato/themes/popular/';

fetch(themes_wp_org_popular)
.then(res => res.json())
.then(wptalk_themes_result => {
  <!-- console.log(wptalk_themes_result); -->
  let output_themes_total =
    '<p>Hiện tại có tổng cộng <a href="https://wordpress.org/themes/" target="_blank" rel="noreferrer"><b>' + wptalk_themes_result.info.results.toString().replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1,") + '</b></a> theme phổ biến được WordPress.org khuyến nghị (loại bỏ trùng lặp).</p>';

  let output_themes =
    '<table><thead><tr><th>Xếp hạng</th><th>Tên</th><th>Phiên bản</th><th>Đánh giá</th><th>Liên kết</th></thead><tbody>';

  for (let t in wptalk_themes_result.themes) {
    id = Number(t) + 1;
    if ( t < 20 ) {
      output_themes +=
        '<tr><td>' +
        id +
        '</td><td>' +
        '<a href="' + wptalk_themes_result.themes[t].homepage + '" target="_blank" rel="noopener noreferrer"_>' +
        truncate(wptalk_themes_result.themes[t].name, 30) +
        '</a>' +
        '</td><td>' +
        wptalk_themes_result.themes[t].version +
        '</td><td>' +
        parseInt(wptalk_themes_result.themes[t].rating) + '/100' +
        '</td><td>' +
        '<a href="' + wptalk_themes_result.themes[t].homepage + '" target="_blank" rel="noopener noreferrer"_>Xem thêm</a>' +
        '</td></tr>';
    }
  }
  output_themes += '</tbody></table>';
  document.getElementById('data-themes-wp-org-popular').innerHTML = output_themes;
  document.getElementById('data-themes-wp-org-total').innerHTML = output_themes_total;  
})

fetch(themes_envato_popular)
.then(res => res.json())
.then(wptalk_themes_popular_result => {
  <!-- console.log(wptalk_themes_popular_result); -->
  let output_themes_popular =
    '<table><thead><tr><th>Xếp hạng</th><th>Tên</th><th>Giá(USD)</th><th>Doanh số</th><th>Đánh giá</th><th>Liên kết</th></thead><tbody>';
  for (let t in wptalk_themes_popular_result) {
    id = Number(t) + 1;
    if ( t < 20 ) {
      output_themes_popular +=
        '<tr><td>' +
        id +
        '</td><td>' +
        '<a href="https://1.envato.market/c/1957476/275988/4415?u=' + wptalk_themes_popular_result[t].url + '" target="_blank">' +
        truncate(wptalk_themes_popular_result[t].item, 30) +
        '</a>' +
        '</td><td>' +
        '$ ' + Math.round( wptalk_themes_popular_result[t].cost ) +
        '</td><td>' +
        wptalk_themes_popular_result[t].sales +
        '</td><td>' +
        Math.round( wptalk_themes_popular_result[t].rating_decimal*20 ) + '/100' +
        '</td><td>' +
        '<a href="https://1.envato.market/c/1957476/275988/4415?u=' + wptalk_themes_popular_result[t].url + '" target="_blank">Xem thêm</a>' +
        '</td></tr>';
    }
  }
  output_themes_popular += '</tbody></table>';
  document.getElementById('data-themes-envato-popular').innerHTML = output_themes_popular;
})

</script>

